// Fun√ß√£o para limpar e formatar a query para o formato do to_tsquery do PostgreSQL
// Ex: "fone bluetooth resistente" -> "fone & bluetooth & resistente"
const formatSearchQuery = (str) => {
  if (!str) return '';
  return str
    .toString()
    .toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "") // Remove acentos
    .trim()
    .split(/\s+/) // Divide em palavras
    .filter(word => word.length > 2) // Remove palavras curtas
    .join(' & '); // Junta com '&' para que todas as palavras sejam buscadas
};

const formattedQuery = formatSearchQuery(customerQuery);

// Retorna a query formatada para ser usada no pr√≥ximo n√≥
return [{ json: { searchQuery: formattedQuery } }];

