// Access the single item from the previous node's output.
const item = $input.first();

// CORRECTION #1: The key is 'Segments' (capital S).
const inputSegments = item.json.Segments;

// Check if the array is valid and has at least one segment.
if (inputSegments && inputSegments.length > 0) {

    // Get the last segment object directly from the array.
    // CORRECTION #2: Remove '.json' as the object is the element itself.
    let lastSegment = inputSegments[inputSegments.length - 1];

    // Check if the last segment's duration is less than 2 seconds.
    if (parseFloat(lastSegment.duration) < 2.0) {

        // Only merge if there are at least two segments to combine.
        if (inputSegments.length > 1) {
            // Get the second-to-last segment.
            // CORRECTION #3: Remove '.json' here as well.
            let secondLastSegment = inputSegments[inputSegments.length - 2];

            // Combine the words from the last segment into the second-to-last one.
            secondLastSegment.words += ' ' + lastSegment.words;

            // Add the durations together.
            secondLastSegment.duration = parseFloat(secondLastSegment.duration) + parseFloat(lastSegment.duration);

            // Update the end time to match the merged segment.
            secondLastSegment.end = lastSegment.end;

            // Remove the now-empty last segment from the array.
            inputSegments.pop();
        }
    }
}

// Return the final, corrected list of segments.
// The output key should also be 'Segments' (capital S) for consistency.
return [{ json: { Segments: inputSegments } }];

// Get the single item from the input.
const item = $input.first();

// CORREÇÃO: The input is a string that looks like a JSON array. We need to parse it.
// Access the string from Segments[0] and convert it into a real array.
const jsonString = item.json.Segments[0];
const inputData = JSON.parse(jsonString);

// Target duration for each scene in seconds.
const splitLength = 5;
const segments = [];
let currentSegment = {
  id: 0,
  words: "",
  duration: 0,
  start: 0,
  end: 0,
};

// Check if the parsed data is a valid array.
if (!Array.isArray(inputData)) {
  throw new Error("Parsed data is not an array. Check the format of the incoming string.");
}

// Set the start time for the very first segment from the first word.
if (inputData.length > 0) {
  currentSegment.start = inputData[0].start;
}

// Loop through each word object in the now-correctly-parsed array
for (const wordObj of inputData) {
  const { word, start, end } = wordObj;

  // If this is the first word of a new segment, set its start time
  if (currentSegment.words === "") {
    currentSegment.start = start;
  }

  // Add the word to the current segment's text
  currentSegment.words += (currentSegment.words ? ' ' : '') + word;

  // Update the end time and duration of the current segment
  currentSegment.end = end;
  currentSegment.duration = parseFloat((currentSegment.end - currentSegment.start).toFixed(2));

  // If the segment's duration meets or exceeds the split length, finalize and store it
  if (currentSegment.duration >= splitLength) {
    segments.push(currentSegment);

    // Reset for the next segment
    currentSegment = {
      id: segments.length,
      words: "",
      duration: 0,
      start: end, // The next segment will start where this one left off
      end: 0,
    };
  }
}

// Add the last remaining segment if it has any words
if (currentSegment.words.length > 0) {
  segments.push(currentSegment);
}

// Calculate total duration and format it
const totalDuration = segments.reduce((acc, seg) => acc + seg.duration, 0);
const roundedTotalDuration = Math.round(totalDuration);
const minutes = Math.floor(roundedTotalDuration / 60);
const seconds = roundedTotalDuration % 60;
const totalRuntimeString = `${minutes} minute${minutes !== 1 ? 's' : ''}, ${seconds} second${seconds !== 1 ? 's' : ''}`;

// Prepare the final output object
const output = {
  Segments: segments,
  totalDuration: roundedTotalDuration,
  TotalMinutes: totalRuntimeString
};

// Return the result
return [{ json: output }];

